import{r,o as l,c as t,a as i,b as o,F as n,d as a,e as c}from"./app.4d0e5ba5.js";import{_ as s}from"./plugin-vue_export-helper.21dcd24c.js";var p="/NaturalLanguageProcessing/ReadingPapers/Hierarchy-AwareGlobalModelForHierarchicalTextClassification/1.png";const h={},d=i("h2",{id:"\u8BBA\u6587\u540D\u79F0",tabindex:"-1"},[i("a",{class:"header-anchor",href:"#\u8BBA\u6587\u540D\u79F0","aria-hidden":"true"},"#"),a(" \u8BBA\u6587\u540D\u79F0")],-1),A={href:"https://aclanthology.org/2020.acl-main.104/?ref=https://githubhelp.com",target:"_blank",rel:"noopener noreferrer"},H=a("Hierarchy-Aware Global Model for Hierarchical Text Classification"),M=c('<h2 id="abstract" tabindex="-1"><a class="header-anchor" href="#abstract" aria-hidden="true">#</a> Abstract</h2><p>\u5206\u5C42\u6587\u672C\u5206\u7C7B\u662F\u5177\u6709\u5206\u7C7B\u5C42\u6B21\u7684\u591A\u6807\u7B7E\u6587\u672C\u5206\u7C7B\u7684\u4E00\u9879\u91CD\u8981\u800C\u5BCC\u6709\u6311\u6218\u6027\u7684\u5B50\u4EFB\u52A1\u3002</p><p>\u73B0\u6709\u65B9\u6CD5\u7684\u95EE\u9898:</p><ul><li>\u5728\u5168\u5C40\u89C6\u56FE\u4E0B\u5BF9\u5206\u5C42\u6807\u7B7E\u7ED3\u6784\u8FDB\u884C\u5EFA\u6A21\u65F6\u5B58\u5728\u56F0\u96BE\u3002</li><li>\u5B83\u4EEC\u4E0D\u80FD\u5145\u5206\u5229\u7528\u6587\u672C\u7279\u5F81\u7A7A\u95F4\u548C\u6807\u7B7E\u7A7A\u95F4\u4E4B\u95F4\u7684\u76F8\u4E92\u4F5C\u7528\u3002</li></ul><p>\u672C\u6587\u6539\u8FDB:</p><ul><li>\u7528\u6709\u5411\u56FE\u7684\u5F62\u5F0F\u8868\u8FBE\u5C42\u6B21\u7ED3\u6784\uFF0C\u5E76\u5F15\u5165\u5C42\u6B21\u611F\u77E5\u7ED3\u6784\u7F16\u7801\u5668\u6765\u5EFA\u6A21\u6807\u7B7E\u4F9D\u8D56\u3002</li><li>\u5728\u5C42\u6B21\u7F16\u7801\u5668\u7684\u57FA\u7840\u4E0A\uFF0C\u63D0\u51FA\u4E86\u4E00\u79CD\u65B0\u9896\u7684\u7AEF\u5230\u7AEF\u5C42\u6B21\u611F\u77E5\u5168\u5C40\u6A21\u578B(HiAGM)\u3002\u591A\u6807\u7B7Eattention\u53D8\u4F53(HiAGM-LA)\u901A\u8FC7\u5C42\u6B21\u7F16\u7801\u5668\u5B66\u4E60\u5C42\u6B21\u611F\u77E5\u7684\u6807\u7B7E\u5D4C\u5165\uFF0C\u5E76\u5BF9\u6807\u7B7E\u611F\u77E5\u7684\u6587\u672C\u7279\u5F81\u8FDB\u884C\u5F52\u7EB3\u878D\u5408\u3002</li><li>\u63D0\u51FA\u4E86\u4E00\u79CD\u6587\u672C\u7279\u5F81\u4F20\u64AD\u6A21\u578B(HiAGM-TP)\u4F5C\u4E3A\u6F14\u7ECE\u53D8\u91CF\uFF0C\u76F4\u63A5\u5C06\u6587\u672C\u7279\u5F81\u63D0\u4F9B\u7ED9\u5C42\u6B21\u7F16\u7801\u5668\u3002\u4E0E\u4E4B\u524D\u7684\u5DE5\u4F5C\u76F8\u6BD4\uFF0CHiAGM-LA\u548CHiAGMTP\u5728\u4E09\u4E2A\u57FA\u51C6\u6570\u636E\u96C6\u4E0A\u90FD\u5B9E\u73B0\u4E86\u663E\u8457\u548C\u4E00\u81F4\u7684\u6539\u8FDB\u3002</li></ul><h2 id="introduction" tabindex="-1"><a class="header-anchor" href="#introduction" aria-hidden="true">#</a> Introduction</h2><p>\u6587\u672C\u5206\u7C7B\u88AB\u5E7F\u6CDB\u5E94\u7528\u4E8E\u81EA\u7136\u8BED\u8A00\u5904\u7406(NLP)\u5E94\u7528\u4E2D\uFF0C\u5982\u60C5\u611F\u5206\u6790(Pang\u548CLee, 2007)\u3001\u4FE1\u606F\u68C0\u7D22(Liu\u7B49\uFF0C2015)\u548C\u6587\u6863\u5206\u7C7B(Yang\u7B49\uFF0C2016)\u3002</p><p>\u5206\u5C42\u6587\u672C\u5206\u7C7B(HTC)\u662F\u4E00\u4E2A\u7279\u6B8A\u7684\u591A\u6807\u7B7E\u6587\u672C\u5206\u7C7B(MLC)\u95EE\u9898\uFF0C\u5176\u4E2D\u5206\u7C7B\u7ED3\u679C\u5BF9\u5E94\u4E8E\u5206\u7C7B\u6CD5\u5C42\u6B21\u7ED3\u6784\u7684\u4E00\u4E2A\u6216\u591A\u4E2A\u8282\u70B9\u3002\u5206\u7C7B\u5C42\u6B21\u7ED3\u6784\u901A\u5E38\u5EFA\u6A21\u4E3A\u6811\u6216\u6709\u5411\u65E0\u73AF\u56FE\uFF0C\u5982\u56FE\u6240\u793A\u3002</p><p><img src="'+p+'" alt="\u5C42\u6B21\u5206\u7C7B" loading="lazy"></p><p>HTC\u73B0\u6709\u7684\u65B9\u6CD5\u53EF\u4EE5\u5206\u4E3A\u4E24\u7C7B\uFF0C\u5C40\u90E8\u65B9\u6CD5\u548C\u5168\u5C40\u65B9\u6CD5\u3002:</p><ul><li>\u5C40\u90E8\u65B9\u6CD5: \u503E\u5411\u4E8E\u6784\u5EFA\u591A\u4E2A\u5206\u7C7B\u6A21\u578B\uFF0C\u7136\u540E\u4EE5\u81EA\u9876\u5411\u4E0B\u7684\u65B9\u5F0F\u904D\u5386\u5C42\u6B21\u7ED3\u6784\u3002\u4E4B\u524D\u7684\u5C40\u90E8\u7814\u7A76\u63D0\u51FA\u901A\u8FC7\u5411\u7236\u8282\u70B9\u5B66\u4E60\u6765\u514B\u670D\u5B50\u8282\u70B9\u4E0A\u7684\u6570\u636E\u4E0D\u5E73\u8861\u3002\u4F46\u662F\uFF0C\u8FD9\u4E9B\u6A21\u578B\u542B\u6709\u5927\u91CF\u7684\u53C2\u6570\uFF0C\u7531\u4E8E\u7F3A\u4E4F\u6574\u4F53\u7684\u7ED3\u6784\u4FE1\u606F\uFF0C\u5BB9\u6613\u5BFC\u81F4\u66B4\u9732\u504F\u5DEE\u3002</li><li>\u5168\u5C40\u65B9\u6CD5: \u5C06HTC\u95EE\u9898\u89C6\u4E3A\u4E00\u4E2A\u5E73\u9762MLC\u95EE\u9898\uFF0C\u5E76\u5BF9\u6240\u6709\u7C7B\u4F7F\u7528\u4E00\u4E2A\u5206\u7C7B\u5668\u3002\u6700\u8FD1\u7684\u5168\u5C40\u65B9\u6CD5\u5F15\u5165\u4E86\u5404\u79CD\u7B56\u7565\u6765\u5229\u7528\u81EA\u4E0A\u800C\u4E0B\u8DEF\u5F84\u7684\u7ED3\u6784\u4FE1\u606F\uFF0C\u5982\u9012\u5F52\u6B63\u5219\u5316\u3001\u5F3A\u5316\u5B66\u4E60\u548C\u5143\u5B66\u4E60\u3002\u76EE\u524D\u8FD8\u6CA1\u6709\u5BF9\u6807\u7B7E\u76F8\u5173\u7279\u5F81\u7684\u6574\u4F53\u6807\u7B7E\u7ED3\u6784\u8FDB\u884C\u7F16\u7801\u7684\u5168\u5C40\u65B9\u6CD5\u3002\u6B64\u5916\uFF0C\u8FD9\u4E9B\u65B9\u6CD5\u4ECD\u7136\u4EE5\u4E00\u79CD\u6D45\u5C42\u7684\u65B9\u5F0F\u5229\u7528\u5C42\u6B21\u7ED3\u6784\uFF0C\u4ECE\u800C\u5FFD\u7565\u5728\u6211\u4EEC\u7684\u5DE5\u4F5C\u4E2D\u5DF2\u7ECF\u8BC1\u660E\u66F4\u6709\u6210\u6548\u7684\u7EC6\u7C92\u5EA6\u6807\u7B7E\u76F8\u5173\u4FE1\u606F\u3002</li></ul><p>\u672C\u6587\u505A\u51FA\u7684\u6539\u8FDB:</p><ul><li>\u5C06\u5C42\u6B21\u7ED3\u6784\u8868\u8FF0\u4E3A\u4E00\u4E2A\u6709\u5411\u56FE\uFF0C\u5E76\u5229\u7528\u6807\u7B7E\u4F9D\u8D56\u7684\u5148\u9A8C\u6982\u7387\u6765\u805A\u5408\u8282\u70B9\u4FE1\u606F\u3002</li><li>\u63D0\u51FA\u4E86\u4E00\u79CD\u57FA\u4E8E\u5C42\u6B21\u611F\u77E5\u7684\u5168\u5C40\u6A21\u578B(HiAGM)\uFF0C\u5229\u7528\u6807\u7B7E\u7ED3\u6784\u7279\u5F81\u589E\u5F3A\u6587\u672C\u4FE1\u606F\u3002\u5B83\u5305\u62EC\u7528\u4E8E\u63D0\u53D6\u6587\u672C\u4FE1\u606F\u7684\u4F20\u7EDF\u6587\u672C\u7F16\u7801\u5668\u548C\u7528\u4E8E\u5206\u5C42\u6807\u7B7E\u5173\u7CFB\u5EFA\u6A21\u7684\u5206\u5C42\u611F\u77E5\u7ED3\u6784\u7F16\u7801\u5668\u3002\u5C42\u6B21\u611F\u77E5\u7ED3\u6784\u7F16\u7801\u5668\u53EF\u4EE5\u662FTreeLSTM\uFF0C\u4E5F\u53EF\u4EE5\u662F\u96C6\u6210\u4E86\u5C42\u6B21\u5148\u9A8C\u77E5\u8BC6\u7684\u5C42\u6B21gcn\u3002\u6B64\u5916\uFF0C\u8FD9\u4E24\u79CD\u7ED3\u6784\u7F16\u7801\u5668\u662F\u53CC\u5411\u8BA1\u7B97\u7684\uFF0C\u5141\u8BB8\u5B83\u4EEC\u4EE5\u81EA\u9876\u5411\u4E0B\u548C\u81EA\u5E95\u5411\u4E0A\u7684\u65B9\u5F0F\u6355\u83B7\u6807\u7B7E\u76F8\u5173\u4FE1\u606F\u3002\u56E0\u6B64\uFF0CHiAGM\u6BD4\u4EE5\u5F80\u7684\u81EA\u9876\u5411\u4E0B\u6A21\u578B\u66F4\u52A0\u5065\u58EE\uFF0C\u80FD\u591F\u7F13\u89E3\u66B4\u9732\u504F\u5DEE\u548C\u6570\u636E\u4E0D\u5E73\u8861\u9020\u6210\u7684\u95EE\u9898\u3002</li></ul><p>\u5728\u672C\u6587\u4E2D\uFF0C\u6211\u4EEC\u5C06\u5C42\u6B21\u7ED3\u6784\u8868\u8FF0\u4E3A\u4E00\u4E2A\u6709\u5411\u56FE\uFF0C\u5E76\u5229\u7528\u6807\u7B7E\u4F9D\u8D56\u7684\u5148\u9A8C\u6982\u7387\u6765\u805A\u5408\u8282\u70B9\u4FE1\u606F\u3002\u63D0\u51FA\u4E86\u4E00\u79CD\u57FA\u4E8E\u5C42\u6B21\u611F\u77E5\u7684\u5168\u5C40\u6A21\u578B(HiAGM)\uFF0C\u5229\u7528\u6807\u7B7E\u7ED3\u6784\u7279\u5F81\u589E\u5F3A\u6587\u672C\u4FE1\u606F\u3002\u5B83\u5305\u62EC\u7528\u4E8E\u63D0\u53D6\u6587\u672C\u4FE1\u606F\u7684\u4F20\u7EDF\u6587\u672C\u7F16\u7801\u5668\u548C\u7528\u4E8E\u5206\u5C42\u6807\u7B7E\u5173\u7CFB\u5EFA\u6A21\u7684\u5206\u5C42\u611F\u77E5\u7ED3\u6784\u7F16\u7801\u5668\u3002\u5C42\u6B21\u611F\u77E5\u7ED3\u6784\u7F16\u7801\u5668\u53EF\u4EE5\u662FTreeLSTM\uFF0C\u4E5F\u53EF\u4EE5\u662F\u96C6\u6210\u4E86\u5C42\u6B21\u5148\u9A8C\u77E5\u8BC6\u7684\u5C42\u6B21GCN\u3002\u6B64\u5916\uFF0C\u8FD9\u4E24\u79CD\u7ED3\u6784\u7F16\u7801\u5668\u662F\u53CC\u5411\u8BA1\u7B97\u7684\uFF0C\u5141\u8BB8\u5B83\u4EEC\u4EE5\u81EA\u9876\u5411\u4E0B\u548C\u81EA\u5E95\u5411\u4E0A\u7684\u65B9\u5F0F\u6355\u83B7\u6807\u7B7E\u76F8\u5173\u4FE1\u606F\u3002\u56E0\u6B64\uFF0CHiAGM\u6BD4\u4EE5\u5F80\u7684\u81EA\u9876\u5411\u4E0B\u6A21\u578B\u66F4\u52A0\u5065\u58EE\uFF0C\u80FD\u591F\u7F13\u89E3\u66B4\u9732\u504F\u5DEE\u548C\u6570\u636E\u4E0D\u5E73\u8861\u9020\u6210\u7684\u95EE\u9898\u3002</p><p>\u4E3A\u4E86\u805A\u5408\u6587\u672C\u7279\u5F81\u548C\u6807\u7B7E\u7ED3\u6784\u7279\u5F81\uFF0C\u63D0\u51FA\u4E86\u4E24\u79CDHiAGM\u53D8\u4F53: \u591A\u6807\u7B7E\u6CE8\u610F\u6A21\u578BHiAGM-LA\u3001\u6587\u672C\u7279\u5F81\u4F20\u64AD\u6A21\u578BHiAGM-TP\uFF0C\u8FD9\u4E24\u4E2A\u53D8\u4F53\u90FD\u662F\u57FA\u4E8E\u7ED3\u6784\u7F16\u7801\u5668\u63D0\u53D6\u5C42\u6B21\u611F\u77E5\u7684\u6587\u672C\u7279\u5F81\u3002 HiAGM-LA\u4EE5\u5F52\u7EB3\u7684\u65B9\u5F0F\u63D0\u53D6\u6807\u7B7E\u6587\u672C\u7279\u5F81\uFF0C\u800CHiAGM-TP\u4EE5\u6F14\u7ECE\u7684\u65B9\u5F0F\u751F\u6210\u6DF7\u5408\u4FE1\u606F\u3002HiAGM-LA\u66F4\u65B0\u8DE8\u6574\u4F53\u5C42\u6B21\u7ED3\u6784\u5D4C\u5165\u7684\u6807\u7B7E\uFF0C\u7136\u540E\u4F7F\u7528\u8282\u70B9\u8F93\u51FA\u4F5C\u4E3A\u611F\u77E5\u5C42\u6B21\u7ED3\u6784\u7684\u6807\u7B7E\u8868\u793A\u3002\u6700\u540E\uFF0C\u5BF9\u6807\u7B7E\u611F\u77E5\u6587\u672C\u7279\u5F81\u8FDB\u884C\u591A\u6807\u7B7E\u6CE8\u610F\u3002\u53E6\u4E00\u65B9\u9762\uFF0CHiAGM-TP\u5728\u4E32\u884C\u6570\u636E\u6D41\u4E2D\u76F4\u63A5\u5229\u7528\u6587\u672C\u7279\u6027\u4F5C\u4E3A\u7ED3\u6784\u7F16\u7801\u5668\u7684\u8F93\u5165\u3002\u56E0\u6B64\uFF0C\u5B83\u5728\u6574\u4E2A\u5C42\u6B21\u7ED3\u6784\u4E2D\u4F20\u64AD\u6587\u672C\u4FE1\u606F\u3002\u6574\u4E2A\u5C42\u6B21\u7ED3\u6784\u4E2D\u6BCF\u4E2A\u8282\u70B9\u7684\u9690\u85CF\u72B6\u6001\u8868\u793A\u7279\u5B9A\u4E8E\u7C7B\u7684\u6587\u672C\u4FE1\u606F\u3002</p><p>paper\u7684\u8D21\u732E\u5982\u4E0B:</p><ul><li>\u5229\u7528\u5148\u9A8C\u7684\u5C42\u6B21\u7ED3\u6784\u77E5\u8BC6\uFF0C\u6211\u4EEC\u91C7\u7528\u81EA\u9876\u5411\u4E0B\u548C\u81EA\u5E95\u5411\u4E0A\u7684\u5178\u578B\u7ED3\u6784\u7F16\u7801\u5668\u5BF9\u6807\u7B7E\u4F9D\u8D56\u5173\u7CFB\u8FDB\u884C\u5EFA\u6A21\uFF0C\u8FD9\u5728\u5206\u5C42\u6587\u672C\u5206\u7C7B\u4E2D\u8FD8\u6CA1\u6709\u7814\u7A76\u8FC7\u3002</li><li>\u63D0\u51FA\u4E86\u4E00\u79CD\u65B0\u9896\u7684\u7AEF\u5230\u7AEF\u5C42\u6B21\u611F\u77E5\u5168\u5C40\u6A21\u578B(HiAGM)\u3002\u6211\u4EEC\u8FDB\u4E00\u6B65\u63D0\u51FA\u4E86\u4E24\u79CD\u57FA\u4E8E\u6807\u7B7E\u7684\u6587\u672C\u7279\u5F81\u7684\u53D8\u4F53\uFF0C\u4E00\u4E2A\u5C42\u6B21\u611F\u77E5\u7684\u591A\u6807\u7B7E\u6CE8\u610F\u6A21\u578B(HiAGM-LA)\u548C\u4E00\u4E2A\u5C42\u6B21\u611F\u77E5\u7684\u6587\u672C\u7279\u5F81\u4F20\u64AD\u6A21\u578B(HiAGM-TP)\u3002</li><li>\u6211\u4EEC\u7684\u7ECF\u9A8C\u8BC1\u660E\uFF0CHiAGM\u7684\u4E24\u79CD\u53D8\u4F53\u5728\u4F7F\u7528\u4E0D\u540C\u7684\u7ED3\u6784\u7F16\u7801\u5668\u65F6\uFF0C\u5728\u5404\u79CD\u6570\u636E\u96C6\u4E0A\u90FD\u5B9E\u73B0\u4E86\u4E00\u81F4\u7684\u6539\u8FDB\u3002\u5728RCV1-V2\u4E0A\uFF0C\u6211\u4EEC\u6700\u597D\u7684\u6A21\u578B\u6BD4\u6700\u5148\u8FDB\u7684\u6A21\u578B\u7684MacroF1\u548CMicro-F1\u5206\u522B\u9AD8\u51FA3.25%\u548C0.66%\u3002</li></ul>',18);function u(G,_){const e=r("ExternalLinkIcon");return l(),t(n,null,[d,i("p",null,[i("a",A,[H,o(e)])]),M],64)}var L=s(h,[["render",u],["__file","Hierarchy-AwareGlobalModelForHierarchicalTextClassification.html.vue"]]);export{L as default};
